import React, { useState } from 'react';

const Home = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [titles, setMovieTitles] = useState([]);
    const [selectedMovie, setSelectedMovie] = useState(null);

    const handleSearch = async () => {
        if (searchQuery.length > 0) {
            try {
                const response = await fetch("http://127.0.0.1:5000/titles-list", {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });

                if (response.ok) {
                    const data = await response.json();  // Parse JSON response
                    setMovieTitles(data);
                    console.log(data);
                } else {
                    console.error('Error fetching movie titles:', response.status);
                }
            } catch (error) {
                console.error('Fetch error:', error);
            }
        }
    };

    return (
        <div className="page-container">
            <div className="inner-container">
                <h1>CineWatch</h1>
                <input
                    type="search"
                    className="search-input"
                    placeholder="Search for movies, shows or anime ..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    onKeyDown={(e) => {
                        if (e.key === "Enter") {
                            handleSearch();
                        }
                    }}
                />

                {/* Display movie titles from search */}
                <ul>
                    {titles.map((title, index) => (
                        <li key={index}>{title}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default Home;
