import React, {useEffect, useState} from 'react';
import './Home.css';
import axios from 'axios'
import SearchBar from "../components/SearchBar";
import {useLocation, useNavigate} from "react-router-dom";

const Home = () => {
    const [inputMovieTitle, setInputMovieTitle] = useState('');
    const [recommendations, setRecommendations] = useState([]);
    const location = useLocation();
    const navigate = useNavigate();
    const searchParams = new URLSearchParams(location.search);
    const query = searchParams.get('search');

    useEffect(() => {
        if (query) {
            search(query);
        }
    }, [query]);

    const search = async (query) => {
        try {
            const response = await axios.get(`/recommend?title=${query}`);
            setRecommendations(response.data);
        } catch (error) {
            console.error('Error fetching recommendations:', error);
        }
    };

    return (
        <div className="page-container">
            <div className="inner-container">
                <h1 className="title">Movie Recommendations</h1>
                <SearchBar onSearch={search} />

                <div className="recommendations-container">
                    {recommendations.movies && recommendations.movies.map((movie, index) => (
                        <div key={index} className="movie-card">
                            <img src={recommendations.posters[index]} alt={movie} className="movie-poster"/>
                            <h2 className="movie-title">{movie}</h2>
                        </div>
                    ))}
                </div>
            </div>
        </div>

    );
};

export default Home;
