import React, { useState } from 'react';
import './Home.css'
import '../components/SearchBar.css'

const Home = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [titles, setMovieTitles] = useState([]);
    const [recommendations, setRecommendations] = useState([]);

    // Fetch movie titles based on the search query
    const handleSearch = async (value) => {
        setSearchQuery(value)
        if (searchQuery.length > 0) {
            try {
                const response = await fetch(`http://127.0.0.1:5000/titles-list?q=${searchQuery}`);
                const data = await response.json();
                setMovieTitles(data);
            } catch (error) {
                console.error('Error fetching movie titles:', error);
            }
        }
    };

    const handleMovieSelect = async (movie) => {
        try {
            const response = await fetch(`http://127.0.0.1:5000/recommend?title=${movie}`);
            const data = await response.json();
            setRecommendations(data);
        } catch (error) {
            console.error('Error fetching recommendations:', error);
        }
    };

    return (
        <div className="page-container">
            <div className="inner-container">
                <h1>CineWatch</h1>

                <input
                    type="search"
                    className="search-input"
                    placeholder="Search for movies ..."
                    value={searchQuery}
                    onChange={(e) => handleSearch(e.target.value)}
                />
                <div className="search-results">
                    {titles.map((title, index) => (
                        <li key={index} onClick={() => handleMovieSelect(title)}>
                            {title}
                        </li>
                    ))}
                </div>


                {recommendations.movies && (
                    <div className="recommendations-container">
                        {recommendations.movies.map((movie, index) => (
                            <div key={index} className="movie-card">
                                <img src={recommendations.posters[index]} alt={movie} />
                                <h2>{movie}</h2>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Home;
